static Widget builder(BuildContext context) { return BlocProvider<EmergencyPatientBloc>(create: (context) => EmergencyPatientBloc(EmergencyPatientState(emergencyPatientModelObj: EmergencyPatientModel()))..add(EmergencyPatientInitialEvent()), child: EmergencyPatientScreen()); } 
@override Widget build(BuildContext context) { return BlocBuilder<EmergencyPatientBloc, EmergencyPatientState>(builder: (context, state) {return SafeArea(child: Scaffold(body: SizedBox(height: SizeUtils.height, width: double.maxFinite, child: Stack(alignment: Alignment.bottomCenter, children: [Align(alignment: Alignment.center, child: Column(mainAxisSize: MainAxisSize.min, children: [SizedBox(height: 5.v), _buildAppBar(context), SizedBox(height: 736.v, width: double.maxFinite, child: Stack(alignment: Alignment.center, children: [Align(alignment: Alignment.center, child: Container(height: 721.v, width: double.maxFinite, decoration: BoxDecoration(color: appTheme.whiteA700, borderRadius: BorderRadius.vertical(top: Radius.circular(25.h))))), _buildSearchMap(context)]))])), CustomImageView(imagePath: ImageConstant.imgMap, height: 721.v, width: 373.h, radius: BorderRadius.vertical(top: Radius.circular(25.h)), alignment: Alignment.bottomCenter, margin: EdgeInsets.only(bottom: 25.v))])), bottomNavigationBar: _buildBottomBar(context)));}); } 
